mapper.select:
SELECT ADOPT_NO,ADOPT_TITLE, ADOPT_CONTENT, THUMNAIL, ADOPT_NAME, ADOPT_AGE, ADOPT_TYPE, ADOPT_ADDRESS, READ_COUNT, MEMBER_NO,MEMBER_NICKNAME,PROFILE_IMG, TO_CHAR(ADOPT_WRITE_DATE,'YYYY-MM-DD HH24:MI:SS') ADOPT_WRITE_DATE, TO_CHAR(ADOPT_UPDATE_DATE,'YYYY-MM-DD HH24:MI:SS') ADOPT_DATE_DATE
,(SELECT COUNT(*) FROM BOOKMARK WHERE ADOPT_NO=#{adoptNo} AND MEMBER_NO=#{memberNo}) BOOKMARK
FROM ADOPT JOIN "MEMBER" USING(MEMBER_NO) WHERE ADOPT_DEL_FL='N' AND BOARD_CODE=1 AND ADOPT_NO=#{adoptNo}

mapper.delete:
DELETE FROM "BOOKMARK"
WHERE MEMBER_NO = #{memberNo}
AND ADOPT_NO = #{adoptNo}

mapper.update:
UPDATE ADOPT SET ADOPT_TITLE = #{adoptTitle}, ADOPT_CONTENT = #{adoptContent},
THUMNAIL = #{thumnail},
ADOPT_NAME = #{adoptName}, ADOPT_AGE = #{adoptAge}, ADOPT_TYPE = #{adoptType}, ADOPT_ADDRESS = #{adoptAddress} WHERE ADOPT_NO = #{adoptNo} AND BOARD_CODE = 1 AND MEMBER_NO = #{memberNo}

mapper.delete:
UPDATE ADOPT
SET ADOPT_DEL_FL = 'Y'
WHERE ADOPT_NO = #{adoptNo}
AND BOARD_CODE = 1
AND MEMBER_NO = #{memberNo}
